import React, { useState, useEffect } from 'react';
import { calculateFinalActivation } from '../utils';

function NeuralNetwork({ inputValues }) {
  const [hiddenNodes, setHiddenNodes] = useState([
    { id: 'hidden1', x: 300, y: 150 },
    { id: 'hidden2', x: 300, y: 250 },
    { id: 'hidden3', x: 300, y: 350 },
    { id: 'hidden4', x: 300, y: 450 },
    // Default hidden nodes
  ]);
  const [outputNode, setOutputNode] = useState({ id: 'output', x: 700, y: 300, activation: 0 }); // Output node
  const [inputNodeValues, setInputNodeValues] = useState({
    input1: 0,
    input2: 0,
    input3: 0,
    // Default input values
  });
  const [edges, setEdges] = useState(generateRandomEdges());

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputNodeValues(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  useEffect(() => {
    // Update input values whenever inputValues prop changes
    setInputNodeValues(inputValues);
  }, [inputValues]);

  useEffect(() => {
    // Update output node activation whenever input node values change
    const activation = calculateFinalActivation(inputNodeValues, hiddenNodes);
    setOutputNode(prevState => ({ ...prevState, activation }));

    // Calculate hidden node values
    const updatedHiddenNodes = hiddenNodes.map(hiddenNode => {
      const value = calculateHiddenNodeValue(hiddenNode, inputNodeValues);
      return { ...hiddenNode, value };
    });
    setHiddenNodes(updatedHiddenNodes);
  }, [inputNodeValues, hiddenNodes]);

  // Function to calculate the value of a hidden node
  const calculateHiddenNodeValue = (hiddenNode, inputNodeValues) => {
    const value = edges
      .filter(edge => edge.target === hiddenNode.id)
      .reduce((acc, edge) => {
        const inputValue = inputNodeValues[edge.source];
        const weight = edge.weight;
        return acc + inputValue * weight;
      }, 0);
    return value;
  };

  useEffect(() => {
    // Update output node activation whenever input node values change
    const hiddenNodeValues = hiddenNodes.map(node => node.value);
    const activation = calculateOutputActivation(hiddenNodeValues);
    setOutputNode(prevState => ({
      ...prevState,
      activation,
      color: activation < 0 ? 'grey' : 'red'
    }));
  }, [hiddenNodes]);

  // Function to calculate the output node activation using ReLU activation function
  const calculateOutputActivation = (hiddenNodeValues) => {
    const summation = edges
      .filter(edge => edge.target === outputNode.id)
      .reduce((acc, edge, index) => {
        const hiddenValue = hiddenNodeValues[index];
        const weight = edge.weight;
        return acc + hiddenValue * weight;
      }, 0);
    return Math.max(0, summation); // Apply ReLU activation function
  };


  function generateRandomEdges() {
    const edges = [];
    const inputNodeIds = Object.keys(inputNodeValues);
    for (let i = 0; i < hiddenNodes.length; i++) {
      const target = hiddenNodes[i].id;
      for (let j = 0; j < inputNodeIds.length; j++) {
        const source = inputNodeIds[j];
        const weight = Math.random() * 2 - 1; // Random weight between -1 and 1
        edges.push({ source, target, weight });
      }
    }
    return edges;
  }

  return (
    <div className="NeuralNetwork">
      <div className="t">Perceptron</div>
      <svg width="1200" height="800">
  {/* Render edges from input nodes to hidden nodes */}
  {[...Array(3).keys()].map((i) => (
    [...Array(4).keys()].map((j) => (
      <g key={`input-hidden-${i}-${j}`}>
        <line
          x1={130}
          y1={(i + 1) * 210}
          x2={370}
          y2={(j + 1) * 175}
          stroke="black"
          strokeWidth="2"
          markerEnd="url(#arrowhead)"
        />
        <foreignObject x={(130 + 350) / 2 - 1} y={(i + 1) * 185 + ((j + 1) * 210 - (i + 1) * 210) / 2 - 10} width="20" height="20">
          <input type="number" style={{ width: "20px", height: "20px" }} />
        </foreignObject>
      </g>
    ))
  ))}

  {/* Render edges from hidden nodes to output node */}
  {[...Array(4).keys()].map((index) => (
    <g key={`hidden-output-${index}`}>
      <line
        x1={429}
        y1={(index + 1) * 176}
        x2={619}
        y2={400}
        stroke="black"
        strokeWidth="2"
        markerEnd="url(#arrowhead)"
      />
      <foreignObject x={(400 + 650) / 2 - 10} y={(index + 1) * 190 + (410 - (index + 1) * 210) / 2 - 10} width="20" height="20">
        <input type="number" style={{ width: "20px", height: "20px" }} />
      </foreignObject>
    </g>
  ))}

  {/* Render input nodes */}
  {Object.keys(inputNodeValues).map((input, index) => (
    <g key={input}>
      <circle cx={100} cy={(index + 1) * 210} r="30" fill="green" opacity="0.96"/>
      <text x={100} y={(index + 1) * 210} fill="white" textAnchor="middle" alignmentBaseline="central">{`I ${index + 1}`}</text>
      {/* Display input value dynamically */}
      <text x={100} y={(index + 1) * 210 + 50} fill="black" textAnchor="middle" alignmentBaseline="central">{inputNodeValues[input]}</text>
    </g>
  ))}


   {/* Render hidden nodes */}
{hiddenNodes.map((node, index) => (
  <g key={`hidden-${index}`}>
    <circle cx={node.x+100} cy={node.y*1.8 - 105} r="30" fill="blue" opacity="0.96"/>
    <text x={node.x+100} y={node.y*1.8 - 105} fill="white" textAnchor="middle" alignmentBaseline="central">{`H ${index + 1}`}</text>
    {/* Display hidden node value dynamically */}
    <text x={node.x+100} y={node.y*1.8 - 55} fill="black" textAnchor="middle" alignmentBaseline="central">{node.value.toFixed(2)}</text>
  </g>
))}



  {/* Render output node */}
<g key={`output`}>
  <circle cx={outputNode.x} cy={outputNode.y} r="30" fill={outputNode.color} opacity="0.96"/>
  <text x={outputNode.x} y={outputNode.y} fill="white" textAnchor="middle" alignmentBaseline="central">Out</text>
  {/* Display output value dynamically rounded to 2 decimal places */}
  <text x={outputNode.x} y={outputNode.y + 50} fill="black" textAnchor="middle" alignmentBaseline="central">
    {outputNode.activation.toFixed(2)}
  </text>
</g>

  {/* Arrowhead marker */}
  <marker
    id="arrowhead"
    markerWidth="10"
    markerHeight="10"
    refX="8"
    refY="3"
    orient="auto"
    fill="black"
  >
    <polygon points="0 0, 10 3, 0 6" />
  </marker>
</svg>

    </div>
  );
}

export default NeuralNetwork;
