// NeuralNetwork.js
import React, { useState, useEffect } from 'react';
import { calculateFinalActivation } from '../utils';

function NeuralNetwork({ inputValues }) {
  // Generate random hidden nodes and edges for testing purposes
  const [hiddenNodes, setHiddenNodes] = useState(generateRandomHiddenNodes());
  const [edges, setEdges] = useState(generateRandomEdges());
  const [finalActivation, setFinalActivation] = useState(0);

  function generateRandomHiddenNodes() {
    const numNodes = Math.floor(Math.random() * 5) + 1; // Generate between 1 to 5 hidden nodes
    const nodes = [];
    for (let i = 0; i < numNodes; i++) {
      nodes.push({ id: `hidden${i + 1}`, x: Math.random() * 500, y: Math.random() * 300 });
    }
    return nodes;
  }

  function generateRandomEdges() {
    const numEdges = Math.floor(Math.random() * (hiddenNodes.length * 2)) + hiddenNodes.length; // Generate edges between input and hidden nodes
    const edges = [];
    const inputNodeIds = Object.keys(inputValues);
    for (let i = 0; i < numEdges; i++) {
      const source = inputNodeIds[Math.floor(Math.random() * inputNodeIds.length)]; // Random input node
      const target = `hidden${Math.floor(Math.random() * hiddenNodes.length) + 1}`; // Random hidden node
      const weight = Math.random() * 2 - 1; // Random weight between -1 and 1
      edges.push({ source, target, weight });
    }
    return edges;
  }

  useEffect(() => {
    // Update hidden nodes and edges whenever inputValues change
    setHiddenNodes(generateRandomHiddenNodes());
    setEdges(generateRandomEdges());
  }, [inputValues]);

  useEffect(() => {
    const activation = calculateFinalActivation(inputValues, hiddenNodes);
    setFinalActivation(activation);
  }, [inputValues, hiddenNodes]);

  return (
    <div className="NeuralNetwork">
      <svg>
        {/* SVG elements for nodes, edges, animations, etc. */}
        {/* Add output node */}
        <text id="outputNode" x="100" y="100" fill="black">Output Node: {finalActivation}</text>
      </svg>
      <button onClick={addHiddenNode}>Add Hidden Node</button>
      {hiddenNodes.map(node => (
        <button key={node.id} onClick={() => removeHiddenNode(node.id)}>Remove {node.id}</button>
      ))}
    </div>
  );
}

export default NeuralNetwork;
