import React, { useState, useEffect } from 'react';
import ThemeSelector from './ThemeSelector';

function PerceptronSVG({ theme }) {
  useEffect(() => {
    applyTheme(theme);
  }, [theme]);

  const applyTheme = (selectedTheme) => {
    const root = document.documentElement;
    switch (selectedTheme) {
      case 'Red':
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #FF4E50, #F9D423)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #FF4E50, #F9D423)');
        root.style.setProperty('--text-color', '#FFFFFF');
        break;
      case 'Beige':
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #FDC830, #F37335)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #FDC830, #F37335)');
        root.style.setProperty('--text-color', '#FFFFFF');
        break;
      case 'Grey':
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #BDC3C7, #2C3E50)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #BDC3C7, #2C3E50)');
        root.style.setProperty('--text-color', '#FFFFFF');
        break;
      case 'Mint':
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #96C93D, #00B09B)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #96C93D, #00B09B)');
        root.style.setProperty('--text-color', '#FFFFFF');
        break;
      case 'Lilac':
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #B06AB3, #4568DC)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #B06AB3, #4568DC)');
        root.style.setProperty('--text-color', '#FFFFFF');
        break;
      case 'Blue':
      default:
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #2980B9, #6DD5FA, #ffffff)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #2980B9, #6DD5FA, #ffffff)');
        root.style.setProperty('--text-color', '#333333');
        break;
    }
  };


  const handleThemeChange = (selectedTheme) => {
    setTheme(selectedTheme);
  };

  const [nodes, setNodes] = useState([
    { id: 'input1', x: 100, y: 100, isDraggable: false },
    { id: 'input2', x: 100, y: 150, isDraggable: false },
    { id: 'input3', x: 100, y: 200, isDraggable: false },
    { id: 'hidden1', x: 300, y: 100, isDraggable: false },
    { id: 'hidden2', x: 300, y: 150, isDraggable: false },
    { id: 'hidden3', x: 300, y: 200, isDraggable: false },
    { id: 'output', x: 500, y: 150, isDraggable: false },
  ]);

  const [edges, setEdges] = useState([
    { source: 'input1', target: 'hidden1' },
    { source: 'input1', target: 'hidden2' },
    { source: 'input1', target: 'hidden3' },
    { source: 'input2', target: 'hidden1' },
    { source: 'input2', target: 'hidden2' },
    { source: 'input2', target: 'hidden3' },
    { source: 'input3', target: 'hidden1' },
    { source: 'input3', target: 'hidden2' },
    { source: 'input3', target: 'hidden3' },
    { source: 'hidden1', target: 'output' },
    { source: 'hidden2', target: 'output' },
    { source: 'hidden3', target: 'output' },
  ]);

  const [draggingNode, setDraggingNode] = useState(null);

  const handleNodeClick = (id) => {
    const updatedNodes = nodes.map(node =>
      node.id === id ? { ...node, isDraggable: !node.isDraggable } : node
    );
    setNodes(updatedNodes);
    setDraggingNode(null); // Reset dragging node
  };

  const handleMouseDown = (e, id) => {
    const index = nodes.findIndex(node => node.id === id);
    if (nodes[index].isDraggable) {
      setDraggingNode(id); // Set dragging node
    }
  };

  const handleMouseMove = (e) => {
    if (draggingNode) {
      const index = nodes.findIndex(node => node.id === draggingNode);
      const newX = e.clientX - e.target.getBoundingClientRect().left;
      const newY = e.clientY - e.target.getBoundingClientRect().top;
      const updatedNodes = [...nodes];
      updatedNodes[index] = { ...updatedNodes[index], x: newX, y: newY };
      setNodes(updatedNodes);
    }
  };

  const handleMouseUp = () => {
    setDraggingNode(null); // Reset dragging node
  };

  useEffect(() => {
    const updatedEdges = edges.map(edge => {
      const sourceNode = nodes.find(node => node.id === edge.source);
      const targetNode = nodes.find(node => node.id === edge.target);
      return {
        ...edge,
        sourceX: sourceNode.x,
        sourceY: sourceNode.y,
        targetX: targetNode.x,
        targetY: targetNode.y,
      };
    });
    setEdges(updatedEdges);
  }, [nodes, edges]);

  return (
    <svg
      width="800"
      height="400"

  style={{ background: 'var(--background-color)' }}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
    >
      {edges.map(edge => (
        <line
          key={`${edge.source}-${edge.target}`}
          x1={edge.sourceX}
          y1={edge.sourceY}
          x2={edge.targetX}
          y2={edge.targetY}
          stroke="#000000"
          strokeWidth="2"
        />
      ))}
      {nodes.map(node => (
        <circle
          key={node.id}
          cx={node.x}
          cy={node.y}
          r="15"
          fill="#ffffff"
          stroke="#000000"
          strokeWidth="2"
          onClick={() => handleNodeClick(node.id)}
          onMouseDown={(e) => handleMouseDown(e, node.id)}
          style={{ cursor: node.isDraggable ? 'pointer' : 'move' }}
        />
      ))}
    </svg>
  );
}

export default PerceptronSVG;
