import React, { useState, useEffect } from 'react';
import ThemeSelector from './ThemeSelector';

function PerceptronSVG() {
  const [theme, setTheme] = useState('Blue');

  useEffect(() => {
    applyTheme();
  }, [theme]);

  const applyTheme = () => {
    const root = document.documentElement;
    switch (theme) {
      case 'Red':
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #FF4E50, #F9D423)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #FF4E50, #F9D423)');
        root.style.setProperty('--text-color', '#FFFFFF');
        break;
      case 'Beige':
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #FDC830, #F37335)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #FDC830, #F37335)');
        root.style.setProperty('--text-color', '#FFFFFF');
        break;
      case 'Grey':
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #BDC3C7, #2C3E50)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #BDC3C7, #2C3E50)');
        root.style.setProperty('--text-color', '#FFFFFF');
        break;
      case 'Mint':
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #96C93D, #00B09B)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #96C93D, #00B09B)');
        root.style.setProperty('--text-color', '#FFFFFF');
        break;
      case 'Lilac':
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #B06AB3, #4568DC)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #B06AB3, #4568DC)');
        root.style.setProperty('--text-color', '#FFFFFF');
        break;
      case 'Blue':
      default:
        root.style.setProperty('--background-color', 'linear-gradient(to bottom, #2980B9, #6DD5FA, #ffffff)');
        root.style.setProperty('--node-color', 'linear-gradient(to bottom, #2980B9, #6DD5FA, #ffffff)');
        root.style.setProperty('--text-color', '#333333');
        break;
    }
  };

  const handleThemeChange = (selectedTheme) => {
    setTheme(selectedTheme);
  };

  // Rest of your component code here
}
