// NeuralNetwork.js
import React, { useState, useEffect } from 'react';
import { calculateFinalActivation } from '../utils';

function NeuralNetwork({ inputValues }) {
  const [hiddenNodes, setHiddenNodes] = useState([
    { id: 'hidden1', x: 300, y: 150 },
    // Initial hidden nodes
    // Format: { id: 'hidden1', x: 300, y: 150 }
  ]);

  const [edges, setEdges] = useState([
    { source: 'input1', target: 'hidden1', weight: 0.5 },
    { source: 'input1', target: 'hidden2', weight: -0.2 },
    { source: 'input2', target: 'hidden1', weight: 0.8 },
    // Add more edges as needed
  ]);

  const addHiddenNode = () => {
    const id = `hidden${hiddenNodes.length + 1}`;
    const newNode = { id, x: 300, y: 150 }; // Default position
    setHiddenNodes([...hiddenNodes, newNode]);
  };

  const removeHiddenNode = (id) => {
    setHiddenNodes(hiddenNodes.filter(node => node.id !== id));
  };

  const handleAnimations = () => {
    // Implement CSS animations for signal/data flow
    const svgElements = document.querySelectorAll('.signal-flow');
    svgElements.forEach(element => {
      element.style.animation = 'flowAnimation 2s linear infinite';
    });
  };

  const handleEdgeWeights = () => {
    // Implement displaying edge weights
    const svg = document.querySelector('svg');
    edges.forEach(edge => {
      const text = document.createElementNS("http://www.w3.org/2000/svg", "text");
      text.setAttribute("x", (edge.sourceX + edge.targetX) / 2);
      text.setAttribute("y", (edge.sourceY + edge.targetY) / 2);
      text.setAttribute("fill", "black");
      text.textContent = edge.weight.toFixed(2); // Assuming edge.weight contains weight value
      svg.appendChild(text);
    });
  };

  const handleReLUActivation = () => {
    // Implement ReLU activation for the output node
    const finalActivation = calculateFinalActivation(inputValues, hiddenNodes);
    const outputNode = document.getElementById('outputNode');
    if (outputNode) {
      outputNode.textContent = `Activation: ${finalActivation}`; // Update text content of output node
    } else {
      console.error('Output node not found');
    }
  };

  // Call functions when component mounts
  useEffect(() => {
    handleAnimations();
    handleEdgeWeights();
    handleReLUActivation();
  }, [inputValues, hiddenNodes]); // Update dependencies to include inputValues and hiddenNodes

  return (
    <div className="NeuralNetwork">
      {/* Render the neural network visualization */}
      <svg>
        {/* Render input nodes */}
        {inputValues.map((value, index) => (
          <circle key={`input${index + 1}`} cx={50} cy={50 * (index + 1)} r={10} fill="blue" />
        ))}
        {/* Render hidden nodes */}
        {hiddenNodes.map(node => (
          <circle key={node.id} cx={node.x} cy={node.y} r={10} fill="green" />
        ))}
        {/* Render edges */}
        {edges.map((edge, index) => (
          <line key={`edge${index}`} x1={50} y1={50 * parseInt(edge.source.replace('input', ''))} x2={node.x} y2={node.y} stroke="black" />
        ))}
        {/* Render output node */}
        <text x={100} y={100} fill="black">Output Node</text>
      </svg>
      {/* Button to add hidden node */}
      <button onClick={addHiddenNode}>Add Hidden Node</button>
      {/* Button to remove hidden node */}
      {hiddenNodes.map(node => (
        <button key={node.id} onClick={() => removeHiddenNode(node.id)}>Remove {node.id}</button>
      ))}
    </div>
  );
}


export default NeuralNetwork;
