// UserInput.js
import React, { useState, useEffect } from 'react';

function UserInput() {
  const [inputValues, setInputValues] = useState({
    input1: 0,
    input2: 0,
    // Add more input values as needed
  });

  const handleInputFocus = (e) => {
    e.target.style.border = '2px solid blue';
  };

  const handleInputBlur = (e) => {
    e.target.style.border = '1px solid black';
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputValues(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div className="UserInput">
      <h2>User Input</h2>
      <input type="number" name="input1" value={inputValues.input1} onChange={handleInputChange} onFocus={handleInputFocus} onBlur={handleInputBlur} />
      <input type="number" name="input2" value={inputValues.input2} onChange={handleInputChange} onFocus={handleInputFocus} onBlur={handleInputBlur} />
      <input type="number" name="input3" value={inputValues.input3} onChange={handleInputChange} onFocus={handleInputFocus} onBlur={handleInputBlur} />
      <input type="number" name="input4" value={inputValues.input4} onChange={handleInputChange} onFocus={handleInputFocus} onBlur={handleInputBlur} />
    </div>
  );
}

export default UserInput;
